/** @format */

import React, { useEffect, useState } from "react";
import { alpha, makeStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import InputBase from "@material-ui/core/InputBase";
import Badge from "@material-ui/core/Badge";
import MenuItem from "@material-ui/core/MenuItem";
import Menu from "@material-ui/core/Menu";
import MenuIcon from "@material-ui/icons/Menu";
import SearchIcon from "@material-ui/icons/Search";
// import AccountCircle from '@material-ui/icons/AccountCircle';
import {
  FavoriteBorderOutlined,
  ShoppingCartOutlined,
} from "@material-ui/icons";
import "./style.css";
import { Link, NavLink } from "react-router-dom";
import { FaRegUser } from "react-icons/fa";
import logo from "../../images/logo/logo.png";
import Login from "../Login";
import GoogleLogin from "react-google-login";
import {
  Modal,
  MaterialInput,
  MaterialButton,
  // DropdownMenu,
} from "../MaterialUi";
import { useDispatch, useSelector } from "react-redux";
import {
  getAllCategory,
  getCartItems,
  getWishlistItems,
  login,
  signout,
  signup as _signup,
} from "../../actions";
import { red } from "@material-ui/core/colors";
import { generatePublicUrl } from "../../urlConfig";
// import MenuHeader from "../MenuHeader";
// import Cart from "../UI/Cart";

// import { BiUserCircle } from "react-icons/bi";

const useStyles = makeStyles((theme) => ({
  grow: {
    flexGrow: 0.6,
    backgroundColor: "white",
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    display: "none",
    [theme.breakpoints.up("sm")]: {
      display: "block",
    },
  },
  // search: {
  //   position: "relative",
  //   borderRadius: "5px",
  //   backgroundColor: "#f5f5f6",
  //   paddingLeft: "60px",
  //   borderRadius: "5px",
  //   border: "3px solid #f5f5f6",
  //   "&:hover": {
  //     backgroundColor: alpha(theme.palette.common.white, 0.25),
  //   },
  //   marginRight: theme.spacing(2),
  //   marginLeft: 0,
  //   width: "100px",
  //   [theme.breakpoints.up("sm")]: {
  //     marginLeft: theme.spacing(4),
  //     width: "500px",
  //   },
  // },
  searchIcon: {
    padding: theme.spacing(0, 19),
    height: "90%",
    position: "absolute",
    pointerEvents: "none",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  inputRoot: {
    color: "inherit",
  },
  inputInput: {
    // padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    // paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create("width"),
    width: "50%",
    [theme.breakpoints.up("md")]: {
      width: "20ch",
    },
  },
  search: {
    borderRadius: "8px",
    display: "none",
    border:"2px solid #696e79",
    marginLeft: "520px",
    [theme.breakpoints.up("md")]: {
      display: "flex",
      color: "#696e79",
      color:"crimson",
    },
  },

  sectionMobile: {
    display: "flex",
    [theme.breakpoints.up("md")]: {
      display: "none",
    },
  },
}));

export default function PrimarySearchAppBar(props) {
  const [loginModal, setLoginModal] = useState(false);
  const [profileC, setProfileC] = useState(true);
  const [signup, setSignup] = useState(false);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [anchorEl, setAnchorEl] = useState(null);
  const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = React.useState(null);
  const category = useSelector((state) => state.category);
  const cart = useSelector((state) => state.cart);
  const classes = useStyles();
  const dispatch = useDispatch();
  const isMenuOpen = Boolean(anchorEl);
  const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);
  const auth = useSelector((state) => state.auth);
  const wish = useSelector((state) => state.wishlist);
  const product = useSelector((state) => state.product);

  const userSignup = () => {
    const user = { firstName, lastName, email, password };
    if (
      firstName === "" ||
      lastName === "" ||
      email === "" ||
      password === ""
    ) {
      return;
    }

    dispatch(_signup(user));
  };

  const userLogin = () => {
    if (signup) {
      userSignup();
      setAnchorEl(null);
    } else {
      dispatch(login({ email, password }));
      setAnchorEl(null);
    }
  };

  const logout = () => {
    dispatch(signout());
  };

  useEffect(() => {
    if (auth.authenticate) {
      dispatch(getCartItems());
      dispatch(getWishlistItems());

      setLoginModal(false);
    }
  }, [auth.authenticate]);

  const handleProfileMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMobileMenuClose = () => {
    setMobileMoreAnchorEl(null);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
    handleMobileMenuClose();
  };

  useEffect(() => {
    dispatch(getAllCategory());
  }, []);

  const renderCategories = (categories) => {
    let myCategories = [];
    for (let category of categories) {
      myCategories.push(
        <li key={category.name}>
          {category.parentId ? (
            <a
              href={`/${category.slug}?cid=${category._id}&type=${category.type}`}>
              {category.name}{" "}
            </a>
          ) : (
            <span>{category.name}</span>
          )}
          {category.children.length > 0 ? (
            <ul>{renderCategories(category.children)}</ul>
          ) : null}
        </li>
      );
    }
    return myCategories;
  };

  const MobileRenderCategories = (categories) => {
    let myCategories = [];
    for (let category of categories) {
      myCategories.push(
        <MenuItem onClick={handleMenuClose} key={category.name}>
          {category.parentId ? (
            <Link
              to={`/${category.slug}?cid=${category._id}&type=${category.type}`}>
              {category.name}{" "}
            </Link>
          ) : (
            <span>{category.name}</span>
          )}
          {category.children.length > 0 ? (
            <ul>{renderCategories(category.children)}</ul>
          ) : null}
        </MenuItem>
      );
    }
    return myCategories;
  };

  // const handleMobileMenuOpen = (event) => {
  //   setMobileMoreAnchorEl(event.currentTarget);
  // };

  const menuId = "primary-search-account-menu";
  const renderLoggedMenu = (
    <Menu
      anchorEl={anchorEl}
      anchorOrigin={{ vertical: "top", horizontal: "right" }}
      id={menuId}
      keepMounted
      transformOrigin={{ vertical: "top", horizontal: "right" }}
      open={isMenuOpen}
      onClose={handleMenuClose}>
      <div className="loggiedinProfile" style={{"height":"10px","width":"10px"}}>
        <h5>Hello</h5>
        <p>{auth.user.fullName} </p>
        <p>{auth.user.email} </p>
      </div>
      <hr />

      <ul className="mobileViewMenu">
        {category.categories.length > 0
          ? MobileRenderCategories(category.categories)
          : null}
      </ul>

      <MenuItem onClick={handleMenuClose}>
        <Link to="/account/orders">Orders</Link>{" "}
      </MenuItem>
      <MenuItem onClick={handleMenuClose}>
        <Link to="/cart">Cart</Link>{" "}
      </MenuItem>
      <MenuItem onClick={handleMenuClose}>
        <Link to="/wishlist">Wishlist</Link>{" "}
      </MenuItem>
      <MenuItem onClick={handleMenuClose}>
        <Link to="/contact">Contact Us</Link>{" "}
      </MenuItem>
      <MenuItem onClick={handleMenuClose}>
        <Link to="/portfolio">About Us</Link>{" "}
      </MenuItem>

      {/* <MenuItem onClick={handleMenuClose}><Link  to="/portfolio">Portfolio</Link> </MenuItem> */}
      <button className="logoutButton" onClick={() => logout()}>
        logout
      </button>
    </Menu>
  );
  const renderNonLoggedMenu = (
    <Menu
      anchorEl={anchorEl}
      anchorOrigin={{ vertical: "top", horizontal: "right" }}
      id={menuId}
      keepMounted
      transformOrigin={{ vertical: "top", horizontal: "right" }}
      open={isMenuOpen}
      onClose={handleMenuClose}>
      <div className="loggiedinProfile">
        <h5>Welcome</h5>
        <p>To access account and manage orders</p>
        <button
          id="login-modal"
          onClick={() => {
            setSignup(false);
            setLoginModal(true);
            setAnchorEl(false);
          }}>
          Login
        </button>
      </div>
      <hr />
      <MenuItem onClick={handleMenuClose}>
        {/* New Customer ?{" "} */}
        <span>
          <p
            onClick={() => {
              setLoginModal(true);
              setSignup(true);
              setAnchorEl(false);
            }}>
            New Customer ? Signup
          </p>
        </span>
      </MenuItem>
      <ul className="mobileViewMenu">
        {category.categories.length > 0
          ? MobileRenderCategories(category.categories)
          : null}
        {/* <li style={{ lineHeight: "48px" }}>Portfolio</li> */}
      </ul>
    </Menu>
  );

  const mobileMenuId = "primary-search-account-menu-mobile";
  const renderMobileMenu = (
    <Menu
      anchorEl={mobileMoreAnchorEl}
      anchorOrigin={{ vertical: "top", horizontal: "right" }}
      id={mobileMenuId}
      keepMounted
      transformOrigin={{ vertical: "top", horizontal: "right" }}
      open={isMobileMenuOpen}
      onClose={handleMobileMenuClose}>
      <MenuItem>
        <IconButton aria-label="show 4 new mails" color="inherit">
          <Badge badgeContent={4} color="secondary">
            <FavoriteBorderOutlined />
          </Badge>
        </IconButton>
        {/* <p>Profile</p> */}
      </MenuItem>
      <MenuItem>
        <p>Add To Cart</p>
      </MenuItem>
      <MenuItem onClick={handleProfileMenuOpen}>
        <IconButton
          aria-label="account of current user"
          aria-controls="primary-search-account-menu"
          aria-haspopup="true"
          color="inherit">
          <FaRegUser />
        </IconButton>
        <p>FavoriteIcon</p>
      </MenuItem>
    </Menu>
  );

  const [searchTerm, setSearchTerm] = useState("");

  const [searchReveal, setSearchReveal] = useState(true);

  return (
    <div className={classes.grow}>
      <AppBar
        position="fixed"
        style={{ backgroundColor: "white", color: "black", zIndex: "30" }}>
        <Toolbar>
          <div className={classes.sectionMobile}>
            <IconButton
              edge="start"
              className={classes.menuButton}
              color="inherit"
              aria-label="open drawer"
              onClick={handleProfileMenuOpen}>
              <MenuIcon />
            </IconButton>
          </div>

          <div className="logosection">
            <span style={{ width: "60px", height: "60px" }}>
              <Link exact to="/" className="">
                <img src={logo} alt="" />
              </Link>
            </span>
            <p></p>
          </div>

          <div className="renderCat">
            <div className="menuHeader1">
              <ul>
                {category.categories.length > 0
                  ? renderCategories(category.categories)
                  : null}
                {/* <li style={{ lineHeight: "48px" }}>Portfolio</li> */}
              </ul>
            </div>
          </div>











{/* //////////////////////////////////////////////////////////////////////////// */}

<>
          <div className={classes.sectionDesktop}>
            <div className={classes.search} style={{ position: "relative" }}>
              {/* <div className="mx-3"> */}
              <div className={classes.searchIcon}>
                <SearchIcon />
              </div>
              {/* </div> */}
              <InputBase
                placeholder="Search"
                classes={{
                  root: classes.inputRoot,
                  input: classes.inputInput,
                }}
                inputProps={{ "aria-label": "search" }}
                onChange={(e) => setSearchTerm(e.target.value)}
                onFocus={() => {
                  setSearchReveal(true);
                }}
                onBlur={() => {
                  setSearchReveal(true);
                }}
              />
              {searchReveal && (
                <div
                  style={{
                    position: "absolute",
                    top: "90%",
                    left: "10px",
                    width: "100%",
